(573, 1, '2018-08-26 17:32:25', '2018-08-26 17:32:25', '<!-- wp:heading {\"level\":3} -->\r\n<h3><span style=\"font-size: 36px;\">ONLINE WAIVER FOR DOMESTIC STUDENTS</span></h3>\r\n<hr /><!-- /wp:heading -->\r\n\r\n<!-- wp:paragraph -->\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: 24px;\"><strong>IMPORTANT: This is for domestic students only.</strong></span><br /><span style=\"font-size: 18px;\">You will need your PRIVATE HEALTH INSURANCE ID card to complete this process.</span><br /><span style=\"font-size: 18px;\">Before proceeding please contact your private health insurance company for answers to the following questions.</span><br /><span style=\"font-size: 18px;\">Private medical insurance covers fully in the Buffalo, New York area for:</span></p>\r\n<p><span style=\"font-size: 18px;\"><!-- /wp:paragraph -->\r\n\r\n<!-- wp:list --></span></p>\r\n<ul>\r\n<li style=\"text-align: left;\"><span style=\"font-size: 18px;\">Physician Visits</span></li>\r\n<li style=\"text-align: left;\"><span style=\"font-size: 18px;\">Lab/X-Rays</span></li>\r\n<li style=\"text-align: left;\"><span style=\"font-size: 18px;\">Hospital Stays</span></li>\r\n<li style=\"text-align: left;\"><span style=\"font-size: 18px;\">Prescriptions</span></li>\r\n</ul>\r\n<p><span style=\"font-size: 18px;\"><!-- /wp:list -->\r\n\r\n<!-- wp:paragraph --></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: 24px;\"><strong>IMPORTANT: Emergency and Urgent Care ONLY does NOT qualify for a waiver of the school medical insurance requirement.</strong></span><br /><span style=\"font-size: 18px;\"><a href=\"https://www.gallagherstudent.com/buffalo\" target=\"_blank\" rel=\"noreferrer noopener\">Domestic Student Online Waiver</a></span></p>\r\n<p><span style=\"font-size: 18px;\"><!-- /wp:paragraph -->\r\n\r\n<!-- wp:heading {\"level\":3} --></span></p>\r\n<h3> </h3>\r\n<h3><span style=\"font-size: 24px;\">ERIE COUNTY MEDICAID &amp; FIDELIS HEALTH INSURANCE</span></h3>\r\n<p><span style=\"font-size: 18px;\"><!-- /wp:heading -->\r\n\r\n<!-- wp:paragraph --></span></p>\r\n<p><span style=\"font-size: 18px;\">Please contact your Medicaid Representative and verify the medical benefits you would have in Erie County. Emergency and Urgent Care only are NOT accepted. Medicaid coverage may be accepted if within 60 miles. Fidelis Care is accepted for waiver if enrollment is in place by the first day of class.</span></p>\r\n<!-- /wp:paragraph -->', 'ONLINE WAIVER FOR DOMESTIC STUDENTS', '', 'publish', 'closed', 'closed', '', 'online-waiver-for-domestic-students', '', '', '2018-08-26 20:08:50', '2018-08-26 20:08:50', '', 461, 'http://localhost:8888/wordpress/?page_id=573', 0, 'page', '', 0),
